import React, { Component } from 'react'
import { compose } from 'recompose'
import { withFirebase } from '../Firebase'



class TodosShowBase extends Component {
    constructor(props) {
        super(props)
        this.children = this.renderTodos(this.props.uid)
    }

    renderTodos = async uid => {
        let todos = await this.props.firebase.doGetTodos(uid)
        let children = todos.map(todo => <p>{todo}</p>)
        console.log(children)
        if (children) return children
    }

    render() {

        //this.renderTodos(this.props.uid)

        return(
            <div>
                <p>Здесь будут выводиться все данные из базы данных</p>
                {this.children}
            </div>
        )
    }

}

const TodosShow = compose(withFirebase)(TodosShowBase)

export default TodosShow
