import React, { Component } from 'react'
import { compose } from 'recompose'
import { withFirebase } from '../Firebase'

const TodoList = ({todos}) => {
    let stopper = [<p>Подождите, данные еще не пришли из Firebase</p>]
    const [todoss, renderTodos] = React.useState(stopper)
    console.log(todoss)
    todos.then(result => {
        let todos = result.map(todo => {return <p>{todo.todo}</p>})
        console.log(renderTodos)
    })


    return(
        <div>
            {todoss}
        </div>
    )
}

let getTodos = async (fun, uid) => {
    let todos = await fun(uid)
    if(todos) return todos
}


class TodosShowBase extends Component {
    constructor(props) {
        super(props)

    }


    render() { 
        return(
            <div>
                <TodoList todos={getTodos(this.props.firebase.doGetTodos, this.props.uid)}/>
            </div>
        )
    }
}

const TodosShow = compose(withFirebase)(TodosShowBase)

export default TodosShow
