import React from 'react'
import TodosShow from '../TodosShow'
import TodoAdd  from '../TodoAdd'
import firebase from '../Firebase'
import { withAuthorization } from '../Session';
import { withFirebase } from '../Firebase';

let uid = localStorage.getItem('uid')
let recognizeTodos = async (uid) => {
  let todos = await firebase.doGetTodos(uid)
  // console.log(todos)
  // let components = todos.map(todo => todo)
  return todos
}
const AccountPage = () => (
  <div>
    <h1>Аккаунт</h1>
    <TodoAdd firebase={firebase} uid={uid}/>
    <TodosShow firebase={firebase} uid={uid} todos={recognizeTodos(firebase, uid)}/>
  </div>
)

const condition = authUser => !!authUser;

export default withAuthorization(condition)(AccountPage)
