import app from 'firebase/app'
import firebase from 'firebase'


const config = {
    apiKey: process.env.REACT_APP_API_KEY,
    authDomain: process.env.REACT_APP_AUTH_DOMAIN,
    databaseURL: process.env.REACT_APP_DATABASE_URL,
    projectId: process.env.REACT_APP_PROJECT_ID,
    storageBucket: process.env.REACT_APP_STORAGE_BUCKET,
    messagingSenderId: process.env.REACT_APP_MESSAGING_SENDER_ID,
  }

console.log(config)

let UserUid = null

class Firebase {
    constructor() {
        app.initializeApp(config)
        this.auth = app.auth()
        this.db = firebase.firestore()
    }

    doCreateUserWithEmailAndPassword = (email, password) =>
        this.auth.createUserWithEmailAndPassword(email, password)

    doSignInWithEmailAndPassword = (email, password) =>
        this.auth.signInWithEmailAndPassword(email, password)

    doSignOut = () => this.auth.signOut()

    doPasswordReset = email => this.auth.sendPasswordResetEmail(email)

    doPasswordUpdate = password =>
        this.auth.currentUser.updatePassword(password)

    doCreateNewInstanceBasedOnAuthUid = async user => {
        console.log(user)
        try {
            const doc = this.db.collection('users').doc(user.uid)
            await doc.set({
                username: user.email,
                todos: []
            })
            console.log({message: 'Created a node between authUser and Clowd Firestore'})
        } catch(e) {
            console.log({message: 'Error creating node between authUser and Clowd Firestore', e})
            // нужно удалить аккаунт и заставить пользователья регистрироваться снова
        }
    }

    doRecognizeUserUid = uid => {
        console.log('я узнал юзерайди', uid)
        UserUid = uid
    }

    doAddTodo = () => {
        return new Promise((resolve, reject) => {

        })
    }
}

export default Firebase
export { UserUid }
