import React, { Component } from 'react'
import { compose } from 'recompose'
import { withFirebase } from '../Firebase'

let resolvedTodo = false

const TodoList = ({todos}) => {
    let stopper = [<p>Подождите, данные еще не пришли из Firebase</p>]
    const [JSXComponents, renderTodos] = React.useState(stopper)
    if (!resolvedTodo) {
        todos.then(result => {
            let resolvedTodos = result.map(todo => {return <p>{todo.todo}</p>})
            console.log('Receiver Data')
            resolvedTodo = true
            renderTodos(resolvedTodos)
        })
    }

    return(
        <div>
            {JSXComponents}
        </div>
    )
}

let setTodos = async (fun, uid, toAddTodo) => {
    let todos = await fun(uid)
    console.log(todos, toAddTodo)
    if(!!toAddTodo) todos.push(toAddTodo)
    if(todos) return todos
}


class TodosShowBase extends Component {
    constructor(props) {
        super(props)
        console.log({props})
    }


    render() { 
        return(
            <div>
                <TodoList todos={setTodos(this.props.firebase.doGetTodos, this.props.uid, this.props.toAddTodo)}/>
            </div>
        )
    }
}

const TodosShow = compose(withFirebase)(TodosShowBase)

export default TodosShow
