import React from 'react'
import TodosShow from '../TodosShow'
import TodoAdd  from '../TodoAdd'
import firebase from '../Firebase'
import { withAuthorization } from '../Session';

let uid = localStorage.getItem('uid')
let recognizeTodos = async uid => {
  console.log(firebase.recognizeTodos = async uid => {
    let todos = await this.props.firebase.doGetTodos(uid)
    let components = todos.map(todo => <p>{todo}</p>)
    return components
})
  // let todos = await firebase.firestore.doGetTodos(uid)
  // console.log(todos)
  // let components = todos.map(todo => todo)
  // return components
}
recognizeTodos(uid)
const AccountPage = () => (
  <div>
    <h1>Аккаунт</h1>
    <TodoAdd firebase={firebase} uid={uid}/>
    <TodosShow firebase={firebase} uid={uid}/>
  </div>
)

const condition = authUser => !!authUser;

export default withAuthorization(condition)(AccountPage)
