import React, {useState} from 'react'
import TodosShow from '../TodosShow'
import TodoAdd  from '../TodoAdd'
import firebase from '../Firebase'
import { withAuthorization } from '../Session'

let uid = localStorage.getItem('uid')

const sendTodo = (todo) => todo.todo

const AccountPage = () => {
  console.log(Date.now())
  const [JSXTodoShow, renderJSXTodoShow] = useState(
    <TodosShow 
      firebase={firebase} 
      uid={uid} 
      toAddTodo={null}
    />
    )

  return (
      <div>
        <h1>Аккаунт</h1>
        <TodoAdd firebase={firebase} uid={uid} onAddTodo={key => {
          let newTodo = JSON.parse(localStorage.getItem(key))
          localStorage.removeItem(key)

          //key=
          renderJSXTodoShow(
            <TodosShow 
              key={Date.parse(newTodo.createdAt)} 
              firebase={firebase} 
              uid={uid} 
              toAddTodo={newTodo.todo}
            />
          )
        }}/>
        {JSXTodoShow}
      </div>
    )
}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(AccountPage)
