import React, {useState} from 'react'
import TodosShow from '../TodosShow'
import TodoAdd  from '../TodoAdd'
import firebase from '../Firebase'
import { withAuthorization } from '../Session'

let uid = localStorage.getItem('uid')

const sendTodo = (todo) => todo.todo

const AccountPage = () => {
  const TodoList = (firebase, uid, todo) => (<TodosShow firebase={firebase} uid={uid} toAddTodo={todo}/>)
  const [JSXTodoShow, renderJSXTodoShow] = useState(TodoList(firebase, uid, nu))
  return (
    <div>
      <h1>Аккаунт</h1>
      <TodoAdd firebase={firebase} uid={uid} onAddTodo={key => {
        let newTodo = JSON.parse(localStorage.getItem(key))
        localStorage.removeItem(key)
        renderJSXTodoShow(<div><TodosShow firebase={firebase} uid={uid} toAddTodo={newTodo}/></div>)
      }}/>
      {JSXTodoShow}
    </div>
  )
}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(AccountPage)
