import React, { Component } from 'react'
import { compose } from 'recompose'
import { withFirebase } from '../Firebase'



class TodosShowBase extends Component {
    constructor(props) {
        super(props)
        this.todos = null
        this.todos = 5
        this.recognizeTodos = async uid => {
            let todos = await this.props.firebase.doGetTodos(uid)
            let components = todos.map(todo => <p>{todo}</p>)
            return components
        }


    }


    render() {
        this.recognizeTodos(this.props.uid)
            .then(result => {
                return(
                <div>
                    {/* {this.todos} */}
                </div>
                )
            })

    }

}

const TodosShow = compose(withFirebase)(TodosShowBase)

export default TodosShow
